mapper.update:
		UPDATE tbl_product    
        
            moddate  = sysdate
            
           , prodname = #{prodName}
            
            
           , PRICE    = #{price}
            
            
           , QUANTITY = #{quantity}
                
        
           
       WHERE prodcod = #{prodcod}

mapper.select:
SELECT * FROM (SELECT rownum rnum, prodcod, prodname, price, quantity, regdate FROM tbl_product WHERE prodcod > 0
ORDER BY prodcod DESC
) WHERE (rnum >= #{pageStart} AND rnum <= #{pageEnd})
